import { proxy, subscribe as valtioSub } from 'valtio/vanilla'
import type { RouterCtrlState } from '../types/controllerTypes'

// -- initial state ------------------------------------------------ //
const state = proxy<RouterCtrlState>({
  history: ['ConnectWallet'],
  view: 'ConnectWallet',
  data: undefined
})

// -- controller --------------------------------------------------- //
export const RouterCtrl = {
  state,

  subscribe(callback: (newState: RouterCtrlState) => void) {
    return valtioSub(state, () => callback(state))
  },

  push(view: RouterCtrlState['view'], data?: RouterCtrlState['data']) {
    if (view !== state.view) {
      state.view = view
      if (data) {
        state.data = data
      }
      state.history.push(view)
    }
  },

  reset(view: RouterCtrlState['view']) {
    state.view = view
    state.history = [view]
  },

  replace(view: RouterCtrlState['view']) {
    if (state.history.length > 1) {
      state.history[state.history.length - 1] = view
      state.view = view
    }
  },

  goBack() {
    if (state.history.length > 1) {
      state.history.pop()
      const [last] = state.history.slice(-1)
      state.view = last
    }
  },

  setData(data: RouterCtrlState['data']) {
    state.data = data
  }
}
import { RouterCtrl } from '../controllers/RouterCtrl'

export const CoreUtil = {
  WALLETCONNECT_DEEPLINK_CHOICE: 'WALLETCONNECT_DEEPLINK_CHOICE',

  WCM_VERSION: 'WCM_VERSION',

  RECOMMENDED_WALLET_AMOUNT: 9,

  isMobile() {
    if (typeof window !== 'undefined') {
      return Boolean(
        window.matchMedia('(pointer:coarse)').matches ||
          /Android|webOS|iPhone|iPad|iPod|BlackBerry|Opera Mini/u.test(navigator.userAgent)
      )
    }

    return false
  },

  isAndroid() {
    return CoreUtil.isMobile() && navigator.userAgent.toLowerCase().includes('android')
  },

  isIos() {
    const ua = navigator.userAgent.toLowerCase()

    return CoreUtil.isMobile() && (ua.includes('iphone') || ua.includes('ipad'))
  },

  isHttpUrl(url: string) {
    return url.startsWith('http://') || url.startsWith('https://')
  },

  isArray<T>(data?: T | T[]): data is T[] {
    return Array.isArray(data) && data.length > 0
  },

  formatNativeUrl(appUrl: string, wcUri: string, name: string): string {
    if (CoreUtil.isHttpUrl(appUrl)) {
      return this.formatUniversalUrl(appUrl, wcUri, name)
    }
    let safeAppUrl = appUrl
    if (!safeAppUrl.includes('://')) {
      safeAppUrl = appUrl.replaceAll('/', '').replaceAll(':', '')
      safeAppUrl = `${safeAppUrl}://`
    }
    if (!safeAppUrl.endsWith('/')) {
      safeAppUrl = `${safeAppUrl}/`
    }
    this.setWalletConnectDeepLink(safeAppUrl, name)
    const encodedWcUrl = encodeURIComponent(wcUri)

    return `${safeAppUrl}wc?uri=${encodedWcUrl}`
  },

  formatUniversalUrl(appUrl: string, wcUri: string, name: string): string {
    if (!CoreUtil.isHttpUrl(appUrl)) {
      return this.formatNativeUrl(appUrl, wcUri, name)
    }
    let safeAppUrl = appUrl
    if (!safeAppUrl.endsWith('/')) {
      safeAppUrl = `${safeAppUrl}/`
    }
    this.setWalletConnectDeepLink(safeAppUrl, name)
    const encodedWcUrl = encodeURIComponent(wcUri)

    return `${safeAppUrl}wc?uri=${encodedWcUrl}`
  },

  async wait(miliseconds: number) {
    return new Promise(resolve => {
      setTimeout(resolve, miliseconds)
    })
  },

  openHref(href: string, target: '_blank' | '_self') {
    window.open(href, target, 'noreferrer noopener')
  },

  setWalletConnectDeepLink(href: string, name: string) {
    try {
      localStorage.setItem(CoreUtil.WALLETCONNECT_DEEPLINK_CHOICE, JSON.stringify({ href, name }))
    } catch {
      console.info('Unable to set WalletConnect deep link')
    }
  },

  setWalletConnectAndroidDeepLink(wcUri: string) {
    try {
      const [href] = wcUri.split('?')
      localStorage.setItem(
        CoreUtil.WALLETCONNECT_DEEPLINK_CHOICE,
        JSON.stringify({ href, name: 'Android' })
      )
    } catch {
      console.info('Unable to set WalletConnect android deep link')
    }
  },

  removeWalletConnectDeepLink() {
    try {
      localStorage.removeItem(CoreUtil.WALLETCONNECT_DEEPLINK_CHOICE)
    } catch {
      console.info('Unable to remove WalletConnect deep link')
    }
  },

  setModalVersionInStorage() {
    try {
      if (typeof localStorage !== 'undefined') {
        localStorage.setItem(CoreUtil.WCM_VERSION, process.env.ROLLUP_WCM_VERSION ?? 'UNKNOWN')
      }
    } catch {
      console.info('Unable to set Web3Modal version in storage')
    }
  },

  getWalletRouterData() {
    const routerData = RouterCtrl.state.data?.Wallet
    if (!routerData) {
      throw new Error('Missing "Wallet" view data')
    }

    return routerData
  }
}
import { proxy, snapshot, subscribe as valtioSub } from 'valtio/vanilla'
import type { EventsCtrlState, ModalEvent, ModalEventData } from '../types/controllerTypes'

// -- helpers ------------------------------------------------------ //
const isEnabled =
  typeof location !== 'undefined' &&
  (location.hostname.includes('localhost') || location.protocol.includes('https'))

// -- initial state ------------------------------------------------ //
const state = proxy<EventsCtrlState>({
  enabled: isEnabled,
  userSessionId: '',
  events: [],
  connectedWalletId: undefined
})

// -- controller --------------------------------------------------- //
export const EventsCtrl = {
  state,

  subscribe(callback: (newEvent: ModalEvent) => void) {
    return valtioSub(state.events, () => callback(snapshot(state.events[state.events.length - 1])))
  },

  initialize() {
    if (state.enabled && typeof crypto?.randomUUID !== 'undefined') {
      state.userSessionId = crypto.randomUUID()
    }
  },

  setConnectedWalletId(connectedWalletId: EventsCtrlState['connectedWalletId']) {
    state.connectedWalletId = connectedWalletId
  },

  click(data: ModalEventData) {
    if (state.enabled) {
      const event = {
        type: 'CLICK' as const,
        name: data.name,
        userSessionId: state.userSessionId,
        timestamp: Date.now(),
        data
      }
      state.events.push(event)
    }
  },

  track(data: ModalEventData) {
    if (state.enabled) {
      const event = {
        type: 'TRACK' as const,
        name: data.name,
        userSessionId: state.userSessionId,
        timestamp: Date.now(),
        data
      }
      state.events.push(event)
    }
  },

  view(data: ModalEventData) {
    if (state.enabled) {
      const event = {
        type: 'VIEW' as const,
        name: data.name,
        userSessionId: state.userSessionId,
        timestamp: Date.now(),
        data
      }
      state.events.push(event)
    }
  }
}
